import React, { useEffect, useState } from "react";
import {
  Button,
  Dimensions,
  Image,
  Modal,
  StyleSheet,
  Switch,
  Text,
  TouchableOpacity,
  View,
} from "react-native";
import Carousel, { Pagination } from "react-native-snap-carousel";
import { WebView } from "react-native-webview";
import {
  get_pictures,
  get_record_status,
  get_video,
  serverUrl,
  start_recording,
  stop_recording,
  get_alarm_status,
  set_alarm_on,
  set_alarm_off,
} from "./camera/requests";

const { width } = Dimensions.get("window");

const IMAGES = [
  {
    name: "pic1",
    image:
      "",
  },
];

export default function Camera() {
  const [isRecording, setIsRecording] = useState(true);
  const [alarm, setAlarm] = useState(false);
  const [images, setImages] = useState(IMAGES);
  const [indexSelected, setIndexSelected] = useState(0);
  const [showVideo, setShowVideo] = useState(false);
  const [videoUrl, setVideoUrl] = useState("https://reactnative.dev/");

  const onSelect = (indexSelected: number) => {
    setIndexSelected(indexSelected);
  };

  useEffect(() => {
    initializeRecording();
    initializeAlarm();
    getImages();
  }, []);

  const initializeRecording = () => {
    get_record_status()
      .then((response) => response.text())
      .then((status) => {
        if (status === "recording") {
          setIsRecording(true);
        } else {
          setIsRecording(false);
        }
      })
      .catch((error) => {
        alert(error);
      });
  };

  const initializeAlarm = () => {
    get_alarm_status()
      .then((response) => response.text())
      .then((status) => {
        if (status === "on") {
          setAlarm(true);
        } else {
          setAlarm(false);
        }
      })
      .catch((error) => {
        alert(error);
      });
  };

  const handleAlarm = (value: boolean) => {
    if (value === true) {
      set_alarm_on()
        .then((status) => {})
        .catch((error) => {
          alert(error);
          setAlarm(!value);
        });
    } else {
      set_alarm_off()
        .then((status) => {})
        .catch((error) => {
          alert(error);
          setAlarm(!value);
        });
    }
    setAlarm(value);
  };

  const handleSwitch = (value: boolean) => {
    if (value === true) {
      start_recording()
        .then((status) => {})
        .catch((error) => {
          alert(error);
          setIsRecording(!value);
        });
    } else {
      stop_recording()
        .then((status) => {})
        .catch((error) => {
          alert(error);
          setIsRecording(!value);
        });
    }
    setIsRecording(value);
  };

  const getImages = () => {
    get_pictures()
      .then((response) => response.json())
      .then((pictures) => {
        setImages(pictures.arr);
      })
      .catch((error) => {
        alert(error);
      });
  };

  const getVideo = async () => {
    const name = images[indexSelected].name.replace(".jpg", ".mp4");
    get_video(name)
      .then((response) => response.text())
      .then((value) => {
        setVideoUrl(value);
        setShowVideo(true);
      })
      .catch((error) => {
        alert(error);
      });
  };

  return (
    <>
      <View
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          right: 0,
          height: 100,
          paddingTop: 25,
          backgroundColor: "white",
          shadowColor: "#ccc",
          shadowOffset: { width: 1, height: 1 },
          shadowOpacity: 10,
          flex: 1,
          flexDirection: "row",
          alignItems: "center",
        }}
      >
        <Image
          resizeMode="contain"
          source={require("./assets/logo.png")}
          style={{ height: 40 }}
        ></Image>
        <Text
          style={{
            color: "black",
            fontSize: 30,
            fontWeight: "bold",
            fontFamily: "lucida grande",
          }}
        >
          Camera
        </Text>
      </View>
      <View style={{ ...styles.container, marginTop: 150 }}>
        <View
          style={{
            ...styles.flex,
            ...styles.spaceBetween,
            alignItems: "flex-start",
            flex: 1 / 5,
          }}
        >
          <Text style={{ ...styles.text, ...styles.mr }}>Camera recording</Text>
          <Switch
            trackColor={{ true: "#8770AE" }}
            onValueChange={handleSwitch}
            value={isRecording}
          />
        </View>
        <View
          style={{
            ...styles.flex,
            ...styles.spaceBetween,
            alignItems: "flex-start",
            flex: 1 / 5,
          }}
        >
          <Text style={{ ...styles.text, ...styles.mr }}>
            Alarm Notification
          </Text>
          <Switch
            trackColor={{ true: "#8770AE" }}
            onValueChange={handleAlarm}
            value={alarm}
          />
        </View>
        <View
          style={{
            flex: 1 / 2,
            alignItems: "center",
          }}
        >
          <Text
            style={{ ...styles.text, ...styles.mb, alignSelf: "flex-start" }}
          >
            Gallery
          </Text>
          <Carousel
            layout="default"
            data={images}
            sliderWidth={width - 60}
            itemWidth={width - 60}
            onSnapToItem={(index) => onSelect(index)}
            renderItem={({ item, index }) => (
              <Image
                key={index}
                resizeMode="contain"
                source={{
                  uri: `data:image/jpg;base64,${item.image}`,
                }}
                style={{ width: "100%", height: "100%" }}
              />
            )}
          />
          <Pagination
            inactiveDotColor="gray"
            dotColor={"orange"}
            activeDotIndex={indexSelected}
            dotsLength={images.length}
            animatedDuration={150}
            inactiveDotScale={1}
          />
          <Button
            color={"#8770AE"}
            title={"Play Video"}
            onPress={getVideo}
          ></Button>
        </View>
      </View>
      <Modal transparent={true} visible={showVideo}>
        <View
          style={{
            backgroundColor: "white",
            marginTop: 40,
            marginLeft: 40,
          }}
        >
          <WebView
            source={{
              uri: `${serverUrl}${videoUrl}`,
            }}
            onMessage={(event) =>
              event.nativeEvent.data === "WINDOW_CLOSED"
                ? setShowVideo(false)
                : {}
            }
          />
          <TouchableOpacity onPress={() => setShowVideo(false)}>
            <Text style={{ fontWeight: "bold", fontSize: 30 }}>X</Text>
          </TouchableOpacity>
        </View>
      </Modal>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    margin: 60,
    marginLeft: 30,
    marginRight: 30,
    backgroundColor: "#fff",
    height: "100%",
    flex: 1,
    flexDirection: "column",
  },
  flex: {
    flex: 1,
    flexDirection: "row",
    alignItems: "center",
  },
  spaceBetween: {
    justifyContent: "space-between",
  },
  text: {
    fontSize: 18,
    fontWeight: "bold",
    fontFamily: "lucida grande",
  },
  pl: {
    paddingLeft: 16,
  },
  pr: {
    paddingRight: 16,
  },
  mr: {
    marginRight: 16,
  },
  mb: {
    marginBottom: 20,
  },
});
